import { v4 as uuidv4 } from "uuid";
import { createSlice } from "@reduxjs/toolkit";

const initialValue = [
  {
    id: 1,
    title: "Mengerjakan Exercise",
    completed: true,
    editing: true,
  },
  {
    id: 2,
    title: "Mengerjakan Assignment",
    completed: false,
    editing: true,
  },
];

export const todoSlice = createSlice({
  name: "todo",
  initialState: {
    todos: initialValue,
  },

  reducers: {
    deleteContent: (state, action) => {
      state.todos = state.todos.filter((item) => item.id !== action.payload);
    },

    addContent: (state, action) => {
      const newData = { id: uuidv4(), ...action.payload };
      state.todos = [...state.todos, newData];
    },

    editContent: (state, action) => {
    //   const newData = state.todos.map((content) => {
    //     if (content.id === action.payload) {
    //       content.title = action.payload;
    //     }
    //     return content;
    //   });
    //   state.todos = [...state.todos, newData];
    },

    checkContent: (state, action) => {
      state.todos = state.todos.map((todo) => {
        if (todo.id === action.payload) {
          return { ...todo, completed: !todo.completed };
        } else {
          return todo;
        }
      });
    },
  },
});

export const { deleteContent, addContent, editContent, checkContent } =
  todoSlice.actions;
export default todoSlice.reducer;
